import React from "react";
import Header from "../../_components/Header/Header";
import styles from "./styles.module.css"

class Verify extends React.Component{
    constructor(props){
        super(props);
        this.state = { value: '', otp1: "", otp2: "", otp3: "", otp4: "", otp5: "", disable: true };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(value1, event) {

        this.setState({ [value1]: event.target.value });
      }
    
      handleSubmit(event) {
    
        // const data = new FormData(event.target);
        console.log(this.state);
        event.preventDefault();
      }
    
      inputfocus = (elmnt) => {
        if (elmnt.key === "Delete" || elmnt.key === "Backspace") {
          const next = elmnt.target.tabIndex - 2;
          if (next > -1) {
    
            elmnt.target.form.elements[next].focus()
          }
        }
        else {
          console.log("next");
         
            const next = elmnt.target.tabIndex;
            console.log(next)
            if (next < 5) {
              elmnt.target.form.elements[next].focus()
            }
        }
    
      }
    render(){
        return(
            <div className={`${styles['body-bg']}`}>
                <Header/>
                <div className="col-12 d-flex flex-column align-items-center justify-content-center">
                    <p>We will send you one time password this email address.</p>
                    <div className={`${styles['message-bb']}`}></div>
                    <div>
                        <p>(example123@gmail.com)</p>
                        <form action="">
                        <div className={`${styles['otp-main']} d-flex`}>
                            <input type="text" name="otp1"  maxLength="1" className="otpInput" autoComplete="off"   tabIndex="1"  onChange={e => this.handleChange("otp1", e)} onKeyUp={e => this.inputfocus(e)}/>
                            <input type="text" name="otp2"  maxLength="1" autoComplete="off" className="otpInput"   tabIndex="2"  onChange={e => this.handleChange("otp2", e)}  onKeyUp={e => this.inputfocus(e)}/>
                            <input type="text" name="otp3"  maxLength="1" autoComplete="off" className="otpInput"   tabIndex="3"  onChange={e => this.handleChange("otp3", e)} onKeyUp={e => this.inputfocus(e)}/>
                            <input type="text" name="otp4"  maxLength="1" autoComplete="off" className="otpInput"   tabIndex="4"  onChange={e => this.handleChange("otp4", e)} onKeyUp={e => this.inputfocus(e)}/>
                        </div>
                        </form>
                    </div>
                </div>
            </div> 
        )
    }
}
export default Verify
