import React from "react";
import Header from "../../_components/Header/Header";
import styles from "./styles.module.css"
import verified from "../../_assets/verified.png"

class Verify extends React.Component{
    constructor(props){
        super(props);
        this.state = { otp1: "", otp2: "", otp3: "", otp4: "", disable: true,submit:0 };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(value1, event) {
        if(isNaN(event.target.value) || event.target.value===" "){
          event.target.value=""
        }else{
        this.setState({ [value1]: event.target.value });
      }}
    
      handleSubmit(event) {
        event.preventDefault();
        this.setState((e)=>({submit:!e.submit}))
        // const data = new FormData(event.target);
        // console.log(this.state);
      }
    
      inputfocus = (elmnt) => {
        if (elmnt.key === "Delete" || elmnt.key === "Backspace") {
          const next = elmnt.target.tabIndex - 2;
          if (next > -1) {
    
            elmnt.target.form.elements[next].focus()
          }
        }
        else {
          if(isNaN(elmnt.target.value) || elmnt.target.value!==""){
            const next = elmnt.target.tabIndex;
            if (next < 4) {
              elmnt.target.form.elements[next].focus()
            }
          }
        }
    
      }
    render(){
        return(
            <div className={`${styles['body-bg']}`}>
                <Header/>
                <div className={`col-12 fs-20 d-flex flex-column align-items-center justify-content-center ${styles['content-body']}`}>
                  {!this.state.submit
                    ?<p className="">We will send you one time password this email address. <div className={`${styles['message-bb']}`}></div></p>
                    :<p className="">Your Email Verification has been successful. Please click on next to proceed.<div className={`${styles['message-bb']}`}></div></p>
                  }
                    {console.log(this.state.submit)}
                    {!this.state.submit?<div>
                        <p>(example123@gmail.com)</p>
                        <form action="">
                        <div className={`${styles['otp-main']} d-flex`}>
                            <input type="text" name="otp1" maxLength="1" autoComplete="off" tabIndex="1" onChange={e => this.handleChange("otp1", e)} onKeyUp={e => this.inputfocus(e)}/>
                            <input type="text" name="otp2" maxLength="1" autoComplete="off" className="otpInput" tabIndex="2" onChange={e => this.handleChange("otp2", e)}  onKeyUp={e => this.inputfocus(e)}/>
                            <input type="text" name="otp3" maxLength="1" autoComplete="off" className="otpInput" tabIndex="3" onChange={e => this.handleChange("otp3", e)} onKeyUp={e => this.inputfocus(e)}/>
                            <input type="text" name="otp4" maxLength="1" autoComplete="off" className="otpInput" tabIndex="4" onChange={e => this.handleChange("otp4", e)} onKeyUp={e => this.inputfocus(e)}/>
                        </div>
                        <button type="submit" className="col-6" onClick={(e)=>this.handleSubmit(e)}>Done</button>
                        </form>
                    </div>:<div className="" style={{marginLeft:"-5%"}}>
                      <img src={verified} alt="" />
                      <button type="submit" className={`col-6 ${styles['submit']}`} onClick={(e)=>this.handleSubmit(e)}>Next</button>
                      </div>}
                </div>
            </div> 
        )
    }
}
export default Verify
